function HexagonShape({ children }) {
  return (
    <div className="relative">
      <svg
        width="200"
        height="200"
        viewBox="0 0 200 200"
        className="absolute inset-0"
      >
        <defs>
          <linearGradient id="strokeGradient" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor="#2E3092" />
            <stop offset="100%" stopColor="#ED1C24" />
          </linearGradient>
        </defs>
        <path
          d="M100 10 L170 50 L170 150 L100 190 L30 150 L30 50 Z"
          fill="#f7f7f7"
          stroke="url(#strokeGradient)"
          strokeWidth="4"
        />
      </svg>
      <div className="relative z-10 w-[200px] h-[200px] flex flex-col items-center justify-center">
        {children}
      </div>
    </div>
  );
}

function ChildCard({ children }) {
  return (
    <div className="bg-[#f7f7f7] border border-[#a7a7a7] border-dashed rounded-lg p-4 w-[220px] text-sm text-[#000000] leading-relaxed">
      {children}
    </div>
  );
}

