function MigrationSection({
  imagePosition,
  title,
  subtitle,
  calPSR,
  bulletPoints,
  buttonText,
  buttonAction,
  imageUrl,
  imageAlt,
  className = "",
  defaultHighlightWords = [],
}) {
  const renderBulletPoint = (point, index) => {
    const text = typeof point === "string" ? point : point.text;
    const highlightWords =
      typeof point === "string"
        ? // Use dynamic defaultHighlightWords instead of hardcoded array
          defaultHighlightWords
        : point.highlightWords || [];

    return (
      <div key={index} className="flex items-start gap-2.5 lg:gap-4">
        <div className="w-5 h-5 lg:w-6 lg:h-6 bg-[#ba0007] rounded-full flex items-center justify-center flex-shrink-0 mt-0.5">
          <ArrowRight className="w-2.5 h-2.5 lg:w-3 lg:h-3 text-white" />
        </div>
        <span>
          {text.split(" ").map((word, wordIndex) => {
            const shouldBeBlue = highlightWords.some((keyword) =>
              word.toLowerCase().includes(keyword.toLowerCase())
            );

            return (
              <span
                key={wordIndex}
                className={shouldBeBlue ? "text-[#2e3092] font-semibold" : ""}
              >
                {word}
                {wordIndex < text.split(" ").length - 1 ? " " : ""}
              </span>
            );
          })}
        </span>
      </div>
    );
  };

  return (
    <div id="test"
      className={`
        flex flex-col lg:${
          imagePosition === "right" ? "flex-row" : "flex-row-reverse"
        } 
        border border-gray-200 lg:border-0
        ${className}
      `}
    >
      {/* Image Side */}
      <motion.div
        initial={{ x: imagePosition === "right" ? -50 : 50, opacity: 0 }}
        whileInView={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.4 }}
        viewport={{ once: false, amount: 0.3 }}
        className="flex-1 order-first lg:order-none"
      >
        <Image
          src={imageUrl}
          alt={imageAlt}
          className={`w-full h-64 lg:h-full object-cover ${
            imagePosition === "right" ? "rounded-l-2xl" : "rounded-r-2xl"
          }`}
        />
      </motion.div>

      {/* Content Side */}
      <motion.div
        initial={{ x: imagePosition === "right" ? 50 : -50, opacity: 0 }}
        whileInView={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.3 }}
        viewport={{ once: false, amount: 0.3 }}
        className="flex-1"
      >
        <div className="px-6 py-4 lg:px-16 lg:py-6 flex flex-col justify-center">
          <h2 className="text-[#2e3092] text-2xl lg:text-4xl font-bold leading-tight mb-1 lg:mb-3">
            {title}
          </h2>

          {subtitle && (
            <p className="text-[#000000] text-base lg:text-lg leading-relaxed mb-4 lg:mb-6">
              {subtitle}
            </p>
          )}

          {calPSR && (
            <div className=" mb-4 lg:mb-6">
              <p className="text-[25px] font-medium">What CalPSR does:</p>
              <p className="text-lg ">{calPSR}</p>
            </div>
          )}

          <div className="mb-6 lg:mb-8 space-y-4">
            <p className="text-[25px] font-medium">Why it matters:</p>
            <div className="grid lg:grid-cols-2 gap-3">
              {bulletPoints.map((point, index) =>
                renderBulletPoint(point, index)
              )}
            </div>
          </div>
          <ButtonLayout
            text={buttonText}
            image={buttonImage}
            hoverImage={buttonImage}
            className={"w-fit"}
          />
        </div>
      </motion.div>
    </div>
  );
}

